import React, { Component } from "react";

class Main extends Component {
  constructor() {
    super();
    this.state = { someInput: "", list: [] };

    this.handleChange = event => {
      this.setState({ someInput: event.target.value });
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    const { someInput, list } = this.state;

    const error = validateInputText(someInput);

    const newList = list;

    return (
      <div>
        {error && <p style={{color : "red"}}>{error}</p>}
        <form onSubmit={this.handleSubmit}>
          <input
            name="some-input"
            className="my-input"
            value={someInput}
            onChange={this.handleChange}
          />
          <button disabled={error !== null}>Add Music</button>
        </form>
        <ul>
          {list.map((value, index) => (
            <li key={index}>
              {value}{" "}
              <button onClick={() => this.deleteOneByOne(newList, index)}>
                {" "}
                âœ–{" "}
              </button>
            </li>
          ))}
        </ul>

        <button onClick={() => this.deleteAllElements(newList)}>
          Delete All
        </button>

        <button onClick={() => this.deleteLastElement(newList)}>
          Delete last element
        </button>
      </div>
    );
  }

  handleSubmit(event) {
    event.preventDefault();
    this.setState(state => ({
      someInput: "",
      list: state.list.concat([state.someInput])
    }));
  }

  deleteLastElement(newList) {
    newList.pop();
    this.setState({ someInput: "", list: newList });
  }

  deleteOneByOne(newList, index) {
    newList.splice(index, 1);
    this.setState({ someInput: "", list: newList });
  }

  deleteAllElements(newList) {
    newList = [];
    this.setState({ someInput: "", list: newList });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.setState(state => ({
      someInput: "",
      list: state.list.concat([state.someInput])
    }));
  }
}

function validateInputText(text) {
  if (text.length === 0) {
    return <h4>Write to enable the button</h4>;
  }
  return null;
}

export default Main;
